import React from "react";
import { todosLosPaises, crearActividad } from "../redux/accion";
import { useDispatch } from "react-redux";
import { useEffect } from "react";
import { useSelector } from "react-redux";
import { useState } from "react";

export default function CrearActividad() {
  const dispatch = useDispatch();
  const [input, setInput] = useState({
    nombre: "",
    dificultad: 0,
    estacion: "",
    horas: 0,
    paises: [],
  });

  const paises = useSelector((state) => state.countries);
  useEffect(() => {
    dispatch(todosLosPaises());
  }, []);

  function handelChange(e) {
    e.preventDefault();
  }

  return (
    <div>
      <div>
        <form>
          <label>Nombre de la Actividad</label>
          <input type="text" />

          <label>Dificultad</label>
          <input type="number" />

          <label>Epoca del Año</label>
          <select name="" id="">
            <option value="">Verano</option>
            <option value="">Invierno</option>
            <option value="">Otoño</option>
            <option value="">Primavera</option>
          </select>
          <label>Duracion (hs)</label>
          <input type="number" />
          <label>Paises donde se puede realizar</label>
          <select name="" id="">
            {paises.map((e) => {
              return <option>{e.name}</option>;
            })}
          </select>

          <button>Crear Actividad</button>
        </form>
      </div>
    </div>
  );
}
