import React from "react";
import { todosLosPaises, crearActividad } from "../redux/accion";
import { useDispatch } from "react-redux";
import { useEffect } from "react";
import { useSelector } from "react-redux";
import { useState } from "react";

export default function CrearActividades() {
  const dispatch = useDispatch();
  const [input, setInput] = useState({
    nombre: "",
    dificultad: 0,
    estacion: [],
    horas: 0,
    paises: [],
  });

  const paises = useSelector((state) => state.countries);
  useEffect(() => {
    dispatch(todosLosPaises());
  }, []);

  function handelEstacion(e) {
    e.preventDefault();
    setInput({ ...input, estacion: [...input, e.target.name] });
  }

  function handelPais(e) {
    e.preventDefault();
    setInput({ ...input, paises: [...input.paises, e.target.value] });
  }

  function handelChange(e) {
    e.preventDefault();
    setInput({ ...input, [e.target.name]: e.target.value });
  }

  function handelSubmit(e) {
    e.preventDefault();
    dispatch(crearActividad(input));
  }
  return (
    <div>
      <div>
        <form>
          <label>Nombre de la Actividad</label>
          <input
            name="nombre"
            type="text"
            value={input.nombre}
            onChange={(e) => handelChange(e)}
          />

          <label>Dificultad</label>
          <input
            type="number"
            name="dificultad"
            value={input.dificultad}
            onChange={(e) => handelChange(e)}
          />

          <label>Epoca del Año</label>
          <select
            name="estacion"
            value={input.estacion}
            onChange={(e) => handelEstacion(e)}
          >
            <option>Verano</option>
            <option>Invierno</option>
            <option>Otoño</option>
            <option>Primavera</option>
          </select>
          <label>Duracion (hs)</label>
          <input
            type="number"
            name="horas"
            value={input.horas}
            onChange={(e) => handelChange(e)}
          />
          <label>Paises donde se puede realizar</label>
          <select
            name="paises"
            value={input.paises}
            onChange={(e) => handelPais(e)}
          >
            {paises.map((e) => {
              return <option>{e.name}</option>;
            })}
          </select>

          <button onClick={(e) => handelSubmit(e)}>Crear Actividad</button>
        </form>
      </div>
    </div>
  );
}
