import React from "react";
import { todosLosPaises, crearActividad } from "../redux/accion";
import { useDispatch } from "react-redux";
import { useEffect } from "react";
import { useSelector } from "react-redux";
import { useState } from "react";

export default function CrearActividades() {
  const dispatch = useDispatch();
  const [input, setInput] = useState({
    nombre: "",
    dificultad: 0,
    temporada: "",
    duracion: 0,
    paises: [],
  });

  const paises = useSelector((state) => state.countries);
  useEffect(() => {
    dispatch(todosLosPaises());
  }, []);

  function handelEstacion(e) {
    e.preventDefault();
    setInput({ ...input, [e.target.name]: e.target.value });
  }

  function handelPais(e) {
    e.preventDefault();
    setInput({ ...input, paises: [...input.paises, e.target.value] });
  }

  function handelChange(e) {
    e.preventDefault();
    setInput({ ...input, [e.target.name]: e.target.value });
  }

  function handelSubmit(e) {
    e.preventDefault();
    dispatch(crearActividad(input));
  }
  return (
    <div>
      <div>
        <form>
          <label>Nombre de la Actividad</label>
          <input
            name="nombre"
            type="text"
            value={input.nombre}
            onChange={(e) => handelChange(e)}
          />
          <label>Dificultad</label>
          <input
            type="number"
            name="dificultad"
            value={input.dificultad}
            onChange={(e) => handelChange(e)}
          />

          <label>Duracion (hs)</label>
          <input
            type="number"
            name="duracion"
            value={input.duracion}
            onChange={(e) => handelChange(e)}
          />
          <label>Paises donde se puede realizar</label>
          <select
            placeholder="pais"
            name="paises"
            value={input.paises}
            onChange={(e) => handelPais(e)}
          >
            {paises.map((e) => {
              return <option>{e.name}</option>;
            })}
          </select>

          <select onChange={(e) => handelEstacion(e)} value={input.temporada}>
            <option name="temporada" value={input.temporada}>
              nada
            </option>
            <option name="verano" value="verano">
              Verano
            </option>
          </select>
        </form>
        {/* <form action="">
          <label>Epoca del A単o</label>

          <input
            type="checkbox"
            name="Verano"
            value="Verano"
            id="Verano"
            onChange={(e) => handelEstacion(e)}
          />

          <input
            type="checkbox"
            name="Invierno"
            value="Invierno"
            id="Invierno"
            onChange={(e) => handelEstacion(e)}
          />

          <input
            type="checkbox"
            name="Primavera"
            value="Primavera"
            id="Primavera"
            onChange={(e) => handelEstacion(e)}
          />

          <input
            type="checkbox"
            name="Oto単o"
            value="Oto単o"
            id="Oto単o"
            onChange={(e) => handelEstacion(e)}
          /> */}
        {/* </form> */}
        <button onClick={(e) => handelSubmit(e)}>Crear Actividad</button>
      </div>
    </div>
  );
}
