import React, { useState } from "react";
import {
  todosLosPaises,
  ordenadoAZ,
  backup,
  ordenPoblacion,
  actividades,
  paisesConActividades,
  filtradoContinentes,
  busqueda,
} from "../redux/accion";
import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { NavLink } from "react-router-dom";
import Card from "./Card";
import Navbar from "../NavBar/NavBar";
export default function Paises() {
  const dispatch = useDispatch();
  const paise = useSelector((state) => state.countries);
  useEffect(() => {
    console.log("useEffect");
    dispatch(todosLosPaises());
  }, [dispatch]);

  const continentes = [
    "Todos",
    "Americas",
    "Europe",
    "Africa",
    "Oceania",
    "Antartic",
    "Asia",
  ];

  const actividad = useSelector((state) => state.actividades);
  useEffect(() => {
    dispatch(actividades());
  }, []);

  const [actualiza, setActualiza] = useState();

  const handleOrden = (e) => {
    dispatch(ordenadoAZ(e.target.value));

    setActualiza(e.target.value);
  };
  const handlerOrdenPoblacion = (e) => {
    dispatch(ordenPoblacion(e.target.value));
    setActualiza(e.target.value);
  };
  const handlerClear = (e) => {
    dispatch(backup());
  };
  const handlerActividad = (e) => {
    dispatch(paisesConActividades(e.target.value));
    setActualiza(e.target.value);
  };
  const handleContinente = (e) => {
    dispatch(filtradoContinentes(e.target.value));
    setActualiza(e.target.value);
  };

  const handlerPais = (e) => {
    e.preventDefault();
    setActualiza(e.target.value);
  };
  const handelSubmit = (e) => {
    e.preventDefault();
    dispatch(busqueda(actualiza));
    setActualiza(e.target.value);
  };

  return (
    <div>
      <div>
        <Navbar />
      </div>

      <div>
        <button onClick={handlerClear}>Recargar</button>
        <select onChange={handleOrden}>
          <option value="Aleatorio">Aleatorio </option>
          <option value="A-Z">A-Z</option>
          <option value="Z-A">Z-A</option>
        </select>

        <select onChange={(e) => handlerOrdenPoblacion(e)}>
          <option value="Aleatorio">Aleatorio</option>
          <option value="Mayor Poblacion">Mayor Poblacion</option>
          <option value="Menor Poblacion"> Menor Poblacion</option>
        </select>
        <select onChange={(e) => handlerActividad(e)}>
          {actividad.map((e) => {
            return <option>{e.nombre}</option>;
          })}
        </select>

        <select onChange={handleContinente}>
          {continentes.map((e) => {
            return <option>{e}</option>;
          })}
        </select>

        <div>
          <button onClick={handelSubmit}>Buscar</button>
          <input type="text" placeholder="Buscar Pais" onChange={handlerPais} />
        </div>
      </div>

      <div>
        {paise.map((e) => {
          return (
            <NavLink to={`countries/${e.id}`}>
              <Card name={e.name} img={e.img} continente={e.continente} />{" "}
            </NavLink>
          );
        })}
      </div>
    </div>
  );
}
