import React from "react";
import { paisPorId } from "../redux/accion";
import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { Actividad } from "./Actividad.jsx";

export default function Detalle(props) {
  const dispatch = useDispatch();

  const elPais = useSelector((state) => state.countryId);
  useEffect(() => {
    dispatch(paisPorId(props.match.params.id));
  }, [dispatch, props.match.params.id]);
  console.log(elPais, "elpaiss", props);
  return (
    <div>
      <h2>{elPais.name}</h2>
      <h4>{elPais.continente}</h4>
      <h4>{elPais.id}</h4>
      <h3>{elPais.onu}</h3>
      <h3>{elPais.capital}</h3>
      <h3>{elPais.subregion}</h3>
      <h3>{elPais.area}</h3>
      <h3>{elPais.poblacion}</h3>
      <h2>Actividades:</h2>
      {elPais.activities.map((e) => {
        return (
          <Actividad
            nombre={e.nombre}
            temporada={e.temporada}
            duracion={e.duracion}
            dificultad={e.dificultad}
          />
        );
      })}
    </div>
  );
}
