import React from "react";
import { todosLosPaises, crearActividad } from "../redux/accion";
import { useDispatch } from "react-redux";
import { useEffect } from "react";
import { useSelector } from "react-redux";
import { useState } from "react";

export default function CrearActividades() {
  const dispatch = useDispatch();
  const [input, setInput] = useState({
    nombre: "",
    dificultad: 0,
    temporada: "",
    duracion: 0,
    pais: [],
  });

  const [errors, seterrors] = useState({});

  function validate(input) {
    let errors = {};

    if (!input.nombre) {
      errors.nombre = "el nombre de la actividad es requerido";
      input.nombre = "el nombre de la actividad es requerido";
    }
    if (!input.dificultad) {
      errors.dificultad = "debe elegir una dificultad";
      input.dificultad = "debe elegir una dificultad";
    }
    if (
      input.dificultad < 1 ||
      input.dificultad > 5 ||
      isNaN(input.dificultad)
    ) {
      errors.dificultad = "no es un Numero valido";
      input.dificultad = "no es un Numero valido";
    }
    if (!input.temporada) {
      errors.dificultad = "seleccione una temporada";
      input.dificultad = "selecciones una temporada";
    }
    // if (
    //   input.temporada !== "Verano" ||
    //   input.temporada !== "Invierno" ||
    //   input.temporada !== "Otoño" ||
    //   input.temporada !== "Primavera"
    // ) {
    //   errors.temporada = "temporada no valida";
    //   input.temporada = "temporada no valida";
    // }
    if (!input.duracion) {
      errors.duracion = "debe ingresar una duracion";
      input.duracion = "debe ingresar una duracion";
    }
    if (input.duracion < 1 || isNaN(input.duracion)) {
      errors.duracion = "duracion no valida";
      input.duracion = "duracion no valida";
    }
    return errors;
  }

  const paises = useSelector((state) => state.countries);
  useEffect(() => {
    dispatch(todosLosPaises());
  }, [dispatch]);

  function handelEstacion(e) {
    e.preventDefault();
    setInput({ ...input, temporada: [...input.temporada, e.target.value] });
    seterrors(validate({ ...input, [e.target.name]: e.target.value }));
  }

  function handelPais(e) {
    e.preventDefault();
    setInput({ ...input, pais: [...input.pais, e.target.value] });
    seterrors(validate({ ...input, [e.target.name]: e.target.value }));
  }

  function handelChange(e) {
    e.preventDefault();
    setInput({ ...input, [e.target.name]: e.target.value });
    seterrors(validate({ ...input, [e.target.name]: e.target.value }));
  }

  function handelSubmit(e) {
    if (!input.nombre) {
      e.preventDefault();
      return "ingrese un nombre";
    }
    if (!input.dificultad) {
      e.preventDefault();
      return "ingrese dificultad";
    }
    if (
      input.dificultad < 1 ||
      input.dificultad > 5 ||
      isNaN(input.dificultad)
    ) {
      e.preventDefault();
      return "no es un Numero valido";
    }
    if (!input.temporada) {
      e.preventDefault();
      return "selecciones una temporada";
    }
    // if (
    //   input.temporada !== "Verano" ||
    //   input.temporada !== "Invierno" ||
    //   input.temporada !== "Otoño" ||
    //   input.temporada !== "Primavera"
    // ) {
    //   e.preventDefault();
    //   return "temporada no valida";
    // }
    if (!input.duracion) {
      e.preventDefault();
      return "debe ingresar una duracion";
    }
    if (input.duracion < 1 || isNaN(input.duracion)) {
      e.preventDefault();
      return "duracion no valida";
    }
    e.preventDefault();
    dispatch(crearActividad(input));
    alert("Tu actividad fue creada exitosamente");
  }
  return (
    <div>
      <div>
        <form>
          <label>Nombre de la Actividad</label>
          <input
            name="nombre"
            type="text"
            value={input.nombre}
            onChange={(e) => handelChange(e)}
          />
          {errors.nombre && <p>{errors.nombre}</p>}
          <label>Dificultad</label>
          <input
            type="number"
            name="dificultad"
            value={input.dificultad}
            onChange={(e) => handelChange(e)}
          />
          {errors.dificultad && <p>{errors.dificultad}</p>}
          <label>Duracion (hs)</label>
          <input
            type="number"
            name="duracion"
            value={input.duracion}
            onChange={(e) => handelChange(e)}
          />
          {errors.duracion && <p>{errors.duracion}</p>}
          <label>Paises donde se puede realizar</label>
          <select
            placeholder="pais"
            name="pais"
            value={input.pais}
            onChange={(e) => handelPais(e)}
          >
            {paises.map((e) => {
              return <option>{e.name}</option>;
            })}
          </select>
        </form>
        <form action="">
          <label>Epoca del Año</label>

          <input
            type="checkbox"
            name="Verano"
            value="Verano"
            id="Verano"
            onChange={(e) => handelEstacion(e)}
          />

          <input
            type="checkbox"
            name="Invierno"
            value="Invierno"
            id="Invierno"
            onChange={(e) => handelEstacion(e)}
          />

          <input
            type="checkbox"
            name="Primavera"
            value="Primavera"
            id="Primavera"
            onChange={(e) => handelEstacion(e)}
          />

          <input
            type="checkbox"
            name="Otoño"
            value="Otoño"
            id="Otoño"
            onChange={(e) => handelEstacion(e)}
          />
          {errors.temporada && <p>{errors.temporada}</p>}
        </form>
        <button onClick={(e) => handelSubmit(e)}>Crear Actividad</button>
      </div>
    </div>
  );
}
