import { ALLCOUNTRIES, COUNTRY_ID } from "./accion";
const initialState = {
  countries: [],
  countryId: [],
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case ALLCOUNTRIES: {
      console.log(action.payload, "este es el estado");
      return {
        ...state,
        countries: action.payload,
      };
    }
    case COUNTRY_ID: {
      console.log(action.payload, "payloaaad");
      let leti = action.payload;
      if (leti.onu === true) leti.onu = "Pertenece";
      else leti.onu = "No Pertenece";
      if (leti.activities.length < 2)
        leti.activities = "No se registran actividades";
      return {
        ...state,
        countryId: leti,
      };
    }
    default:
      return state;
  }
};
export default rootReducer;
