import {
  ALLCOUNTRIES,
  COUNTRY_ID,
  CREAR_ACTIVIDAD,
  ORDEN_AZ,
  BACK,
} from "./accion";
const initialState = {
  countries: [],
  countryId: [],
  backup: [],
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case ALLCOUNTRIES: {
      console.log(action.payload, "este es el estado");
      return {
        ...state,
        countries: action.payload,
      };
    }
    case BACK: {
      return {
        ...state,

        backup: action.payload,
      };
    }
    case COUNTRY_ID: {
      console.log(action.payload, "payloaaad");
      let leti = action.payload;
      if (leti.onu === true) leti.onu = "Pertenece";
      else leti.onu = "No Pertenece";
      if (leti.activities.length < 1) leti.activities = false;
      return {
        ...state,
        countryId: leti,
      };
    }
    case CREAR_ACTIVIDAD: {
      return {
        ...state,
      };
    }
    case ORDEN_AZ: {
      const ordenado = action.payload;
      let estado = state.countries;
      let ordenados;
      if (ordenado === "A-Z") {
        ordenados = estado.sort((a, b) => {
          if (a.name < b.name) return -1;
          if (a.name > b.name) return 1;
          return 0;
        });
      }
      if (ordenado === "Z-A") {
        ordenados = estado.sort((a, b) => {
          if (a.name > b.name) return -1;
          if (a.name < b.name) return 1;
          return 0;
        });
      }
      if (ordenado === "Aleatorio") ordenados = state.backup;
      return {
        ...state,
        countries: ordenados,
      };
    }
    default:
      return state;
  }
};
export default rootReducer;
